import React, { useState } from "react";
import Header from "./Header";
import Footer from "./Footer";
import Note from "./Note";
import CreateArea from "./CreateArea";
import axios from 'axios';

function App() {
  const [notes, setNotes] = useState([]);
  const [newId, setNewId] = useState(0);
  async function addNote(newNote){
      try{
        const result = await axios.post(`http://localhost:3000/routes/`,{...newNote,id: newId});
        if(result){
          console.log("Note is added to DB");
          console.log("the added id is "+ newId);
        }
        setNewId(newId + 1);
        getNotes();

    }
    catch (error) {
      console.error('A problem occurred adding or updating a record: ', error);
    }
  }

  async function deleteNote(title,content) {
      try{
        const result = await axios.delete(`http://localhost:3000/routes/`,{params:{title:title,content:content}});
        if(result){
          console.log("Note is deleted from DB");
        }
        getNotes();
      }
      catch (error) {
        console.error('A problem occurred while deleting a record: ', error);
      }

    }
    async function getNotes() {
      try{
        const result = await axios.get(`http://localhost:3000/routes/`);
        if(result){
          console.log("Notes are returned");
          console.log(result.data);
        }
        setNotes(result.data);
        console.log(notes);
      }
      catch (error) {
        console.error('A problem occurred while deleting a record: ', error);
      }

    }
    return (
      <div onLoad={getNotes}>
        <Header />
        <CreateArea  onAdd={addNote} />
        {notes.map((noteItem,index) => {
          return (
            <Note
              key={index}
              id={index}
              title={noteItem.title}
              content={noteItem.content}
              onDelete={deleteNote}
            />
          );
        })}
        <Footer />
      </div>
    );
}

export default App;
