import React, { useState } from "react";
import Header from "./Header";
import Footer from "./Footer";
import Note from "./Note.jsx";
import CreateArea from "./CreateArea";
import axios from 'axios';

function App() {
  const [notes, setNotes] = useState([]);

async function addNote(newNote) {
    try{
        const result = await axios.post(`https://localhost:${process.env.port}/routes/`,newNote);
      if(result){
        console.log("Note is added to DB" + result);
      }
      setNotes(prevNotes => {
        return [...prevNotes, newNote];
      });

  }
  catch (error) {
    console.error('A problem occurred adding or updating a record: ', error);
  }
}

async function deleteNote(id) {
    try{
      const result = await axios.delete(`https://localhost:${process.env.port}/routes/${id}`);
      if(result){
        console.log("Note is deleted from DB");
      }
      setNotes(prevNotes => {
        return prevNotes.filter((noteItem, index) => {
          return index !== id;
        });
      });
    }
    catch (error) {
      console.error('A problem occurred while deleting a record: ', error);
    }

  }

  return (
    <div>
      <Header />
      <CreateArea onAdd={addNote} />
      {notes.map((noteItem, index) => {
        return (
          <Note
            key={index}
            id={index}
            title={noteItem.title}
            content={noteItem.content}
            onDelete={deleteNote}
          />
        );
      })}
      <Footer />
    </div>
  );
}

export default App;
